mapper.insert:
insert into order (address_id, order_time, `state`, 
  total, total_integral, user_id
  )
values (#{addressId}, #{orderTime}, #{state},
  #{total}, #{totalIntegral}, #{userId}
  )

mapper.select:
select id, address_id, order_time, `state`, total, total_integral, user_id
from order
where id = #{id}
