mapper.update:
update permission

  `name` = #{name},
   url = #{url},
   father_id = #{fatherId}

where permission_id = #{permissionId}

mapper.select:
select permission_id, `name`, url, father_id
from permission
where permission_id = #{permissionId}

select permission.permission_id, permission.name, permission.url, permission.father_id
from permission,role_permission
where permission.permission_id=role_permission.permissione_id and role_permission.role_id=#{roleId}
